INSERT INTO `qs_hrtool_category` VALUES
  (1, '招聘/面试', 0, 1, '包含各种人事招聘和人才面试专用表格文档，招聘专员可根据需求自行下载hr资料，实现人企互聘第一步。'),
  (2, '人事档案', 0, 1, '以表单和文字形式记录企业和员工的主要经历、政治面貌、素质文化等内容，作为企业文化的内在凭证。 '),
  (3, '员工管理', 0, 1, '包含工作内容、员工纪律等一系列直接涉及员工福利和工作性质的资料。帮助hr快速专业的落实人才工作。'),
  (4, '合同/试用', 0, 1, 'hr下载合适的合同范本作为跟人才之间的契约协议签订，成功实现企业和人才之间的双向选择。'),
  (5, '薪酬制度', 0, 1, 'hr可以适时参考工具箱内的资料表单，制定合理人性的薪酬制度，在众多招聘企业中一招制胜。'),
  (6, '加班/考勤', 0, 1, '工具箱内提供各种形式的考勤表格供hr参考，以维护企业正常的工作秩序，严肃企业纪律。'),
  (7, '出差', 0, 1, '针对公司出差问题，提供相关的出差规定、出差情况记录表等，准确掌握员工工作动态，明晰公司财务清单。'),
  (8, '员工培训', 0, 1, '提供各类员工培训参考，包含出国培训、新进职员培训等，为企业提供多样的培训意见，提高员工素质能力。'),
  (9, '绩效考核', 0, 1, '多种绩效考核办法标准，为企业提供全面的参考，正面引导员工的工作行为和工作业绩，实现企业战略目标。 '),
  (10, '辞退/移交', 0, 1, '工具箱内提供各种辞职申请范本，企业可以进行参考，制定合理的离职流程。'),
  (11, '其它', 0, 1, '企业hr工作中其他事项的参考标准，可以自定义选择添加。');
